#include "log_manager.h"#include <filesystem>#include <string>#include <gtest/gtest.h>namespace {    using std::string;    using std::filesystem::path;    using std::optional;    using service::LogManager;    // Pseudo testing    TEST(LogManagerTest, LogManagerTest) {        optional<path> logFile = path("./test.log");        LogManager::getInstance().initialize("LogManagerTest", logFile);        auto logger = LogManager::getInstance().getLogger();        string message = "Hello world!, this is sample {} log message";        logger->trace(message, "trace");        logger->debug(message, "debug");        logger->info(message, "info");        logger->warn(message, "warn");        logger->error(message, "error");        logger->critical(message, "critical");        LogManager::getInstance().uninitialize();    }} // namespace